import React, { useCallback, useState } from 'react';
import AppLayout from "../components/AppLayout";
import Head from 'next/head'
import {Form, Input, Checkbox, Button } from 'antd'
import styled from 'styled-components'
import useInput from '../components/hooks/useInput'

const ErrorMessage = styled.div`
    color:red;`

const Signup = () => {
   // 커스텀 훅으로 중복 제거
    const [id, onChangeId] = useInput('');
    const [password, onChangePassword] = useInput('');
    const [nickname, onChangeNickname] = useInput('');
    const [passwordCheck, setPasswordCheck] = useState('');
    const [passwordError, setPasswordError] = useState(false);
    const onChangePasswordCheck = useCallback((e) =>{
      setPasswordCheck(e.target.value);
      setPasswordCheck(e.target.value !== password)
    }, [password]);


    const [term, setTerm] = useState('');
    const [termError, setTermError] = useState(false);
    const onChangeTerm = useCallback((e)=> {
      setTerm(e.target.checked)
      setTermError(false);
    })

    

  const onSubmit = useCallback(() =>{

  },[]);

  return (
  <AppLayout>
     <Head>
      <meta charset="utf-8"/>
      <title>signup</title>
    </Head>
    <Form onFinish={onSubmit}>
      <div>
        <label htmlFor="user-id">아이디</label>
        <br />
        <Input value={id} required onChange={onChangeId}/>
      </div>
      <div>
        <label htmlFor="user-nickname">닉네임</label>
        <br />
        <Input value={nickname} required onChange={onChangeNickname}/>
      </div>
      <div>
        <label htmlFor="user-password">비밀번호</label>
        <br />
        <Input value={password} type="password" required onChange={onChangePassword}/>
      </div>
      <div>
        <label htmlFor="user-password-check">비밀번호 체크</label>
        <br />
        <Input type='password' 
        value={passwordCheck} required onChange={setPasswordCheck}/>
        {passwordError && <ErrorMessage>비밀번호가 일치하지 않습니다.</ErrorMessage>}
      </div>
      <div>
        <Checkbox name="user-term" checked={term} onChange={onChangeTerm}>
          가을짱말을들어 !
        </Checkbox>
        {termError && <ErrorMessage>동의해야함</ErrorMessage>}
      </div>
      <div style={{marginTop:10}}>
        <Button type="primary" htmlType='submit'>가입하기</Button>
      </div>
      </Form>

  </AppLayout>)
};
export default Signup;
