const initialState = {
  user :{
    isLoggedIn : false,
    user: null,
    signUpData: {},
    loginData: {},
  },
  post :{
    mainPosts: [],
  }
};

export const loginAction = (data) =>{
  return{
    type: 'LOG_IN',
    data
  }
}

export const logoutAction = () =>{
  return{
    type: 'LOG_OUT'
  }
}

// 초기 state
const changeNickname=(data)=> {
  return{
    type: 'CHANGE_NICKNAME',
    data,
  }
  
}
// 이전 상태와 액션을 받아 다음 상태를 생성
const rootReducer = (state, initialState, action)=> {
    switch (action, type){
      case 'LOG_IN':
        return {
          ...state,
          user: {
            ...state.user,
            isLoggedIn : true,
            user: action.data,
          }
        };
        case 'LOG_OUT':
        return {
          ...state,
          user: {
            ...state.user,
            isLoggedIn : false,
            user: null,
          }
        }
    }
};

export default rootReducer;
